import React, { useCallback, useReducer, useState } from 'react'
import {Dropdown} from 'react-bootstrap';
import { Container } from 'react-bootstrap';


//Adding a props field so we can toggle between dark and light dropdown menu


const Sort = ({movies, setMovies}) => {
    const [state, setState] = useState('');

    const changeSort = useCallback((event) => {
        switch (event) {
            case 'Popularity Ascending':
                movies.sort((firstElement, secondElement) => {
                    return firstElement.popularity - secondElement.popularity;
                })
                setState({sort: 'Popularity Ascending'})
                setMovies(movies);
                break;

            case 'Popularity Descending':
                movies.sort((firstElement, secondElement) => {
                    return secondElement.popularity - firstElement.popularity;
                })
                setState({sort: 'Popularity Descending'})
                setMovies(movies);
                break;
            case 'Ratings Descending':
                movies.sort((firstElement, secondElement) => {
                    return secondElement.ratings - firstElement.ratings;
                })
                setState({sort: 'Ratings Descending'});
                setMovies(movies);
                break;

            case 'Release Date Descending':
                movies.sort((firstElement, secondElement) => {
                    return Date(firstElement.release_date) - Date(secondElement.release_date);
                })
                setState({sort: 'Release Date Descending'});
                setMovies(movies);
                break;

            case 'Ratings Ascending':
                movies.sort((firstElement, secondElement) => {
                    return firstElement.ratings - secondElement.ratings;
                })
                setState({sort: 'Ratings Ascending'});
                setMovies(movies);
                break;

            case 'Release Date Ascending':
                movies.sort((firstElement, secondElement) => {
                    return Date(secondElement.release_date) - Date(firstElement.release_date);
                })
                setState({sort: 'Release Date Ascending'});
                setMovies(movies);

                break;
            default:
                throw new Error();
        }
    })



    return (
        <div>
            <Container>
                <Dropdown onSelect={changeSort}>
                    <Dropdown.Toggle size="lg" variant="danger" id="dropdown-basic" >
                        Sort By
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item eventKey="Popularity Ascending" href="#/p-ascending" >Popularity Ascending</Dropdown.Item>
                        <Dropdown.Item eventKey="Ratings Ascending" href="#/r-ascending" >Ratings Ascending</Dropdown.Item>
                        <Dropdown.Item eventKey="Release Date Ascending" href="#/m-ascending">Release Date Ascending</Dropdown.Item>
                        <Dropdown.Item eventKey="Popularity Descending" href="#/p-descending">Popularity Descending</Dropdown.Item>
                        <Dropdown.Item eventKey="Ratings Descending" href="#/r-descending">Ratings Descending</Dropdown.Item>
                        <Dropdown.Item eventKey="Release Date Descending" href="#/m-descending">Release Date Descending</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
            </Container>
        </div>
    )
};

export default Sort;
